mapper.select:
SELECT LECTURE_NO, LECTURE_NAME, LECTURE_TEACHER, LECTRUE_PERIOD, LECTURE_PRICE, LECTURE_CATEGORY_MAINCATEGORY, LECTURE_CATEGORY_SUBCATEGORY, LECTURE_CONTENTS, LECTURE_THUMBNAIL, LECTURE_CREDATE, LECTURE_ACADEMY
FROM LECTURE
WHERE LECTURE_CATEGORY_MAINCATEGORY = #{PARAM1}

SELECT LECTURE_NO, LECTURE_NAME, LECTURE_TEACHER, LECTRUE_PERIOD, LECTURE_PRICE, LECTURE_CATEGORY_MAINCATEGORY, LECTURE_CATEGORY_SUBCATEGORY, LECTURE_CONTENTS, LECTURE_THUMBNAIL, LECTURE_CREDATE, LECTURE_ACADEMY
FROM LECTURE
WHERE LECTURE_CATEGORY_MAINCATEGORY = #{PARAM1} AND LECTURE_CATEGORY_SUBCATEGORY = #{PARAM2}
